import axios from "axios";

const ADD_EMPLOYEE_API_URL = "http://localhost:9091/api/v1/addemployee";
const GET_EMPLOYEE_API_URL = "http://localhost:9091/api/v1/employees";
const DELETE_EMPLOYEE_API_URL = "http://localhost:9091/api/v1/delemployees";
class EmployeeService {
    saveEmployee(employee) {
        return axios.post(ADD_EMPLOYEE_API_URL, employee);
    }
    showEmployees() {
        return axios.get(GET_EMPLOYEE_API_URL);
    }
    deleteEmployee(id){
        return axios.delete(GET_EMPLOYEE_API_URL + "/" + id);
    }
}
export default new EmployeeService();
