import React, { useEffect, useState } from 'react'
import { useNavigate } from 'react-router-dom';
import EmployeeService from '../services/EmployeeService';
import Employee from './Employee';

const ShowEmployee = () => {
    const navigate = useNavigate();

    const [loading, setLoading] = useState(true);
    const [employees, setEmployees] = useState(null);

    useEffect(() => {
        const fetchData = async () => {
            setLoading(true);
            try {
                const response = await EmployeeService.showEmployees();
                setEmployees(response.data);
            } catch (err) {
                console.log(err);
            }
            setLoading(false);
        };
        fetchData();
    }, [])

    const deleteEmployee = (e, id) => {
        e.preventDefault();
        EmployeeService.deleteEmployee(id)
    }

    return (
        <div className='container mx-auto my-8'>
            <div className='h-12'>
                <button onClick={() => navigate("/addEmployee")} className='rounded bg-slate-600 text-white px-6 py-2 font-semibold'>Add Employee</button>
            </div>
            <div className='flex shadow-lg border-b'>
                <table className='min-w-full'>
                    <thead className='bg-gray-50'>
                        <tr>
                            <th className='text-left font-medium text-gray-500 uppercase tracking-wider py-3 px-6'>#</th>
                            <th className='text-center font-medium text-gray-500 uppercase tracking-wider py-3 px-6'>First Name</th>
                            <th className='text-center font-medium text-gray-500 uppercase tracking-wider py-3 px-6'>Last Name</th>
                            <th className='text-center font-medium text-gray-500 uppercase tracking-wider py-3 px-6'>Email Id</th>
                            <th className='text-center font-medium text-gray-500 uppercase tracking-wider py-3 px-6'>Actions</th>
                        </tr>
                    </thead>
                    {!loading && (
                        <tbody className='bg-white'>
                            {employees.map((emp) => (
                                <Employee emp={emp} deleteEmployee={deleteEmployee} key={emp.id} />
                            ))}
                        </tbody>
                    )}
                </table>
            </div>
        </div>
    )
}

export default ShowEmployee;
